class SolutionReversePairs {
    public int reversePairs(int[] nums) {
        if (nums == null || nums.length <= 1) return 0;
        long[] A = new long[nums.length];
        for (int i = 0; i < nums.length; i++) A[i] = nums[i];
        return mergeSort(A, 0, nums.length - 1);
    }

    private int mergeSort(long[] A, int start, int end) {
        if (start == end) return 0;
        int mid = start + (end - start) / 2;
        int left = mergeSort(A, start, mid);
        int right = mergeSort(A, mid + 1, end);
        int ret = left + right + findCount(A, start, mid, end);
        merge(A, start, mid, end);
        return ret;
    }
    private void merge(long[] A, int start, int mid, int end) {
        long[] temp = new long[A.length];
        for (int k = start; k <= end; k++) {
            temp[k] = A[k];
        }
        int i = start, j = mid + 1;
        for (int k = start; k <= end; k++) {
            if (i > mid) A[k] = temp[j++];
            else if (j > end) A[k] = temp[i++];
            else if (temp[i] < temp[j]) A[k] = temp[i++];
            else A[k] = temp[j++];
        }
    }
    private int findCount(long[] A, int start, int mid, int end) {
        int count = 0;
        for (int j = mid + 1; j <= end; j++) {
            long target = A[j] * 2;
            int pos = binarySearch(A, start, mid, target);
            if (pos == -1) continue;
            count += mid - pos + 1;
        }
        return count;
    }
    private int binarySearch(long[] A, int start, int end, long target) {
        // find > target first
        while (start + 1 < end) {
            int mid = start + (end - start) / 2;
            if (A[mid] > target) {
                end = mid;
            } else {
                start = mid;
            }
        }
        if (A[start] > target) return start;
        if (A[end] > target) return end;
        return -1;
    }
}





